from python_scripts.call_chat_gpt import perform_llm_tasksfrom python_scripts.process_google_spreadsheet import process_google_sheetfrom datetime import datetimedef main():    current_time = datetime.now()    log = f"Log for task at {str(current_time)}"    log += process_google_sheet()    log += perform_llm_tasks()    with open('./logs/'+str(current_time)+'.txt', 'w') as f:        f.write(log)        main()